export const fetchSuggestions = (searchTerm) => {
  return fetch(`http://localhost:3002/search?q=${searchTerm}`)
  .then((res) => res.json())
  .catch((error)=>error)
};

export const fetchProductDetail = (id) => {
  return fetch(`http://localhost:3002/products/${id}`).then((res) => res.json())
  .catch((error)=>error);
};
