export const fetchSuggestions = (searchTerm) => {
  return fetch(`http://localhost:3001/search?q=${searchTerm}`)
  .then((response)=>{
    if (response.ok) {      
      return {errorMessage:'', result:response.json()}
    } else {
      return {errorMessage:response.statusText, result:[]}
    }


  })
  .catch((error) => {
    console.log(error)
  });
};

export const fetchProductDetail = (id) => {
   fetch(`http://localhost:3001/products/${id}`)
  .then((response)=>{
    if (response.ok) {      
      return {errorMessage:'', result:response.json()}
    } else {
      return {errorMessage:response.statusText, result:[]}
    }

  })
  .catch((error) => {
    console.log(error)
  });
};
