import React, { useState, useEffect } from "react";
import { fetchProductDetail } from "./utils/api";
import { useSelector } from 'react-redux'

import "./ProductDetail.css";

function ProductDetail() {
  const [productInfo, setProductInfo] = useState(null);
  const productId = useSelector(state => state.data.productID)
  const [showProduct, setShowProduct] = useState(false); 
  const [errorMessage, setErrorMessage] = useState("");
  const [loading, setLoding] = useState(false);

  useEffect(() => {
    if (!productId) return;   
    fetchProductDetail(productId).then((productInfo) =>{     
      if (typeof productInfo === 'string' )
      {
        setShowProduct(false)
        setErrorMessage(`Server error!!.Please contact administrator.Error message: ${productInfo}`)
       }
       else
       {
        setShowProduct(true)
        setProductInfo(productInfo)
       } 

    }
    )
      .catch((error) => {
        setShowProduct(false)
        setErrorMessage(`Server error !!.Please contact administrator.Error message: ${error}`)
      });
  }, [productId]);

  const renderProductInfo = () => {
    console.log('renderProductInfo',errorMessage)
    return (
      <>
      {errorMessage ===''?(
      <div className="detail-container">
        <div className="row">
          <img src={productInfo.image} className="product-image" />
        </div>
        <div className="row">
          <div className="row-title">Name:</div>
          <div className="row-body">{productInfo.title}</div>
        </div>
        <div className="row">
          <div className="row-title">Description:</div>
          <div className="row-body">{productInfo.description}</div>
        </div>
        <div className="row">
          <div className="row-title">Price:</div>
          <div className="row-body">{productInfo.price}</div>
        </div>
      </div>
      ):(
        <div class="error-message">
            <em>{errorMessage}</em>
      </div>
      )
    }
      </>
    );
  };

  return  renderProductInfo();
}

export default ProductDetail;
