export const fetchSuggestions = (searchTerm) => {
  return fetch(`http://localhost:3001/search1?q=${searchTerm}`)
  .then(status)
  .then((res) => res.json())
  .catch(error => {
    throw(error);
})
};

export const fetchProductDetail = (id) => {
  return fetch(`http://localhost:3001/products/${id}`)
  .then(status)
  .then((res) => res.json())  
  .catch(error => {
    throw(error);
})
};
