import React, { useEffect, useState } from "react";
import { fetchSuggestions } from "./utils/api";
import "./Autocomplete.css";
import { SaveID } from './redux/action'
import { useSelector, useDispatch } from 'react-redux'
import AwesomeDebouncePromise from 'awesome-debounce-promise';
import useConstant from 'use-constant';
import { useAsync } from 'react-async-hook';

// Generic reusable hook
/*const useDebouncedSearch = (searchFunction) => {

  // Handle the input text state
  const [inputText, setInputText] = useState('');

  // Debounce the original search async function
  const debouncedSearchFunction = useConstant(() =>
  AwesomeDebouncePromise(searchFunction, 300)
  );

  // The async callback is run each time the text changes,
  // but as the search function is debounced, it does not
  // fire a new request on each keystroke
  const searchResults = useAsync(
    async () => {
      if (inputText.length === 0) {
        return [];
      } else {
        return debouncedSearchFunction(inputText);
      }
    },
    [debouncedSearchFunction, inputText]
  );

  // Return everything needed for the hook consumer
  return {
    inputText,
    setInputText,
    searchResults,
  };
}; */

//const useSearchProduct = () => useDebouncedSearch(text => searchProductAsync(text))
function Autocomplete() {

  const [searchTerm, setSearchTerm] = useState("");
  // const { searchTerm, setSearchTerm, searchResults } = useSearchProduct();
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggestions, setshowSuggestions] = useState(false);
  // const productID = useSelector(state => state.data.productID)
  const dispatch = useDispatch()
  const maxSize = 10



 /* useEffect(() => {
   // AwesomeDebouncePromise(searchFunction, 300)
  }, [searchTerm]); */


  const setSuggestion = suggestion => {
    setSearchTerm(suggestion.title)
    setshowSuggestions(false)
    dispatch(SaveID(suggestion.id))
    //console.log('props.productID', productID)
  }
  const searchFunction = fetchSuggestions(searchTerm) 
  const debouncedSearchFunction = AwesomeDebouncePromise(searchFunction, 300)
  const setSearch = async searchTerm => {
    setSearchTerm(searchTerm)
    searchTerm === '' ? setshowSuggestions(false) : setshowSuggestions(true)
    const result = await debouncedSearchFunction(searchTerm);
    setSuggestions(result.slice(0, maxSize))
  }





  return (

    <div className="search-container">
      <input
        type="text"
        value={searchTerm}
        className="search-box"
        placeholder="Search for a product"
        onChange={(e) => setSearch(e.target.value)}
      />
      {(showSuggestions && suggestions) && (

        <ul class="suggestions">
          {suggestions.map((suggestion, index) => {

            return (
              <li key={suggestion.id} onClick={() => setSuggestion(suggestion)}>
                {suggestion.title}
              </li>
            );
          })}
        </ul>


      )}

    </div>


  );
}



export default Autocomplete;
